import React, { Component } from 'react'
import '../App.css';
import GameItem from '../components/GameItem'
import Form from '../components/Form'
import { connect } from 'react-redux'
import { fetchGames } from '../actions/index'


// import { addNewTodo } from '../actions/index'



function searchingFor(search) {
    return function (x) {
        return x.first.toLowerCase().includes(search.toLowerCase()) || !search;
    }
}
class Game extends Component {

    componentDidMount() {
        this.props.fetchGames()
    }

    constructor(props) { 
        super(props);   
        this.state = { 
            game: [],
            search: ""
        };
        this.searchHandler = this.searchHandler.bind(this);
    }

    searchHandler(event){
        this.setState({ search: event.target.value })
    }


    render() {
        const {search, game} = this.state;
        const games = this.props.games.map((game, i) => <GameItem key={i} game={game} />)
        const gameFilter = game.filter(searchingFor(search)).map((game, i) => <GameItem key={i} game={game} />)

        // debugger
        return (
            <div>
                <form className="form-inline my-2 my-lg-0">
                    <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" onChange={this.searchHandler} value/>
                    <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
                </form>
                <div className="wrapper">{games}</div>
                <Form handleOnSubmit={this.addNewTodo} />
            </div>
        )
    }
}

const mapStateToProps = state => {
    // debugger
    return {
        games: state.games
    }
}

export default connect(mapStateToProps, { fetchGames })(Game)
