import React, { Component } from 'react'
import '../App.css';
import GameItem from '../components/GameItem'
import Form from '../components/Form'
import { connect } from 'react-redux'
import { fetchGames } from '../actions/index'


// import { addNewTodo } from '../actions/index'

class Game extends Component {

    componentDidMount() {
        this.props.fetchGames()
    }

    constructor(props) { 
        super(props);   
        this.state = { 
            game: [],
            search: ""
        };
    }

    gameChange = e => {
        this.setState({ search: e.target.value});
    }

    renderGame = game => {
        const { search } = this.state;

        if (search !== "" && game.filter(game => game.title).toLowerCase().indexOf( search.toLowerCase() ) === -1 ){
            return null
        }

    };


    render() {
        const { search } = this.state;
        const games = this.props.games.map((game, i) => <GameItem key={i} game={game} />)
        const filteredGames = games.filter(game => {
            return game.title.toLowerCase().indexOf(search.toLowerCase()) !== -1;
        });

        // debugger
        return (
            <div>
                <div className="row">
                    {filteredGames.map(country => {
                        return this.renderCountry(country);
                    })}
                </div>
                <form className="form-inline my-2 my-lg-0">
                    <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" onChange={this.gameChange}/>
                    <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
                </form>

                <div className="wrapper">{games}</div>
                <Form handleOnSubmit={this.addNewTodo} />
            </div>
        )
    }
}

const mapStateToProps = state => {
    // debugger
    return {
        games: state.games
    }
}

export default connect(mapStateToProps, { fetchGames })(Game)
