import React, { Component } from 'react'
import '../App.css';
import GameItem from '../components/GameItem'
import Form from '../components/Form'
import { connect } from 'react-redux'
import { fetchGames } from '../actions/index'


// import { addNewTodo } from '../actions/index'

class Game extends Component {

    componentDidMount() {
        this.props.fetchGames()
    }

    constructor(props) { 
        super(props);   
        this.state = { 
            game: [],
            searchTerm: ''
        };
    }

    editSearchTerm = (e) => {
        this.setState({ searchTerm: e.target.value })
    }

    dynamicSearch = () => {
        return this.state.games.filter(game => game.toLowerCase().includes(this.state.searchTerm.toLowerCase()))
    }

    render() {
        const games = this.props.games.map((game, i) => <GameItem key={i} game={game} />)

        // debugger
        return (
            <div>
                <input type='text' value={this.state.searchTerm} onChange={this.editSearchTerm} placeholder='Search for a name!' />
                <form className="form-inline my-2 my-lg-0">
                    <input className="form-control mr-sm-2" type="text" placeholder="Search" aria-label="Search" onChange={this.editSearchTerm}/>
                    <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
                </form>
                <div className="wrapper">{games}</div>
                <Form handleOnSubmit={this.addNewTodo} />
            </div>
        )
    }
}

const mapStateToProps = state => {
    // debugger
    return {
        games: state.games
    }
}

export default connect(mapStateToProps, { fetchGames })(Game)
