import React, { Component } from 'react'
import '../App.css';
import GameItem from '../components/GameItem'
import Form from '../components/Form'
import FilterResults from 'react-filter-search'
import { connect } from 'react-redux'
import { fetchGames } from '../actions/index'


// import { addNewTodo } from '../actions/index'

class Game extends Component {

    componentDidMount() {
        this.props.fetchGames()
    }

    constructor(props) { 
        super(props);   
        this.state = { 
            game: [],
            value: ''
        };
    }


    handleChange = event => {
        const { value } = event.target;
        this.setState({ value });
    };


    render() {
        const games = this.props.games.map((game, i) => <GameItem key={i} game={game} />)
        const { game, value } = this.state;

        // debugger
        return (
            <div>

                <input type="text" value={value} onChange={this.handleChange} />
                <SearchResults
                    value={value}
                    game={game}
                    renderResults={results => (
                        <div>
                            {results.map(el => (
                                <div>
                                    <span>{el.name}</span>
                                    <span>{el.email}</span>
                                </div>
                            ))}
                        </div>
                    )}
                />

                <div className="wrapper">{games}</div>
                <Form handleOnSubmit={this.addNewTodo} />
            </div>
        )
    }
}

const mapStateToProps = state => {
    // debugger
    return {
        games: state.games
    }
}

export default connect(mapStateToProps, { fetchGames })(Game)
